INSERT INTO `wp_options` VALUES("261", "_transient_popmake_theme_styles", "/* Popup Google Fonts */\r\n@import url(\'//fonts.googleapis.com/css?family=Acme|Montserrat\');\r\n\r\n/* Popup Theme 28: Framed Border */\r\n.pum-theme-28, .pum-theme-framed-border { background-color: rgba( 255, 255, 255, 0.5 ) } \r\n.pum-theme-28 .pum-container, .pum-theme-framed-border .pum-container { padding: 18px; border-radius: 0px; border: 20px outset #dd3333; box-shadow: 1px 1px 3px 0px rgba( 2, 2, 2, 0.97 ) inset; background-color: rgba( 255, 251, 239, 1 ) } \r\n.pum-theme-28 .pum-title, .pum-theme-framed-border .pum-title { color: #000000; text-align: left; text-shadow: 0px 0px 0px rgba( 2, 2, 2, 0.23 ); font-family: inherit; font-size: 32px; line-height: 36px } \r\n.pum-theme-28 .pum-content, .pum-theme-framed-border .pum-content { color: #2d2d2d; font-family: inherit } \r\n.pum-theme-28 .pum-content + .pum-close, .pum-theme-framed-border .pum-content + .pum-close { height: 20px; width: 20px; left: auto; right: -20px; bottom: auto; top: -20px; padding: 0px; color: #ffffff; font-family: Acme; font-size: 20px; line-height: 20px; border: 1px none #ffffff; border-radius: 0px; box-shadow: 0px 0px 0px 0px rgba( 2, 2, 2, 0.23 ); text-shadow: 0px 0px 0px rgba( 0, 0, 0, 0.23 ); background-color: rgba( 0, 0, 0, 0.55 ) } \r\n/* Popup Theme 27: Cutting Edge */\r\n.pum-theme-27, .pum-theme-cutting-edge { background-color: rgba( 0, 0, 0, 0.5 ) } \r\n.pum-theme-27 .pum-container, .pum-theme-cutting-edge .pum-container { padding: 18px; border-radius: 0px; border: 1px none #000000; box-shadow: 0px 10px 25px 0px rgba( 2, 2, 2, 0.5 ); background-color: rgba( 30, 115, 190, 1 ) } \r\n.pum-theme-27 .pum-title, .pum-theme-cutting-edge .pum-title { color: #ffffff; text-align: left; text-shadow: 0px 0px 0px rgba( 2, 2, 2, 0.23 ); font-family: Sans-Serif; font-size: 26px; line-height: 28px } \r\n.pum-theme-27 .pum-content, .pum-theme-cutting-edge .pum-content { color: #ffffff; font-family: inherit } \r\n.p