INSERT INTO `wp_postmeta` VALUES("1479", "184", "credits_1", "<h6>Credits</h6>\r\n<small>Concept, tekst &amp; spel: Wart Kamps. Regie: Bram Jansen. Vormgeving: Robin Vogel. Choreografie: Ryan Djojokarso. Dramaturgie: Pol Eggermont. Productie: Alles voor de Kunsten. Première: 20 oktober 2016 Toneelschuur Haarlem. Tournee t/m 22 januari 2017. </small>");
