INSERT INTO `wp_postmeta` VALUES("1805", "197", "alinea_4", "<em>Swan Lake</em> is de eerste dansvoorstelling van Jakop Ahlbom in samenwerking met ICKamsterdam, onder leiding van Emio Greco en Pieter C. Scholten in hun nieuwe gezamenlijke huistheater de Meervaart.\r\n\r\n<em>Swan Lake</em> speelt van dinsdag 21 t/m donderdag 30 juni 2016 in de Rode Zaal van De Meervaart, première vrijdag 24 juni 2016. Najaar 2016 volgt een tournee langs meer dan 40 grote theaters in Nederland.");
