INSERT INTO `wp_options` VALUES("814", "_transient_dash_88ae138922fe95674369b1cb3d215a2b", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2016/09/wordpress-4-6-1-security-and-maintenance-release/\'>WordPress 4.6.1 Security and Maintenance Release</a> <span class=\"rss-date\">September 7, 2016</span><div class=\"rssSummary\">WordPress 4.6.1 is now available. This is a security release for all previous versions and we strongly encourage you to update your sites immediately. WordPress versions 4.6 and earlier are affected by two security issues: a cross-site scripting vulnerability via image filename, reported by SumOfPwn researcher Cengiz Han Sahin; and a path traversal vulnerability in [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://poststatus.com/ask-post-status-innovation-wordpress-draft-podcast/\'>Post Status: Ask Post Status: Innovation in WordPress — Draft podcast</a></li><li><a class=\'rsswidget\' href=\'http://heropress.com/essays/custom-not-synonymous-expensive/\'>HeroPress: Custom is not Synonymous with Expensive</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/take-the-2016-git-users-survey\'>WPTavern: Take the 2016 Git User’s Survey</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\"dashboard-news-plugin\"><span>Popular Plugin:</span> Google Analytics Dashboard for WP&nbsp;<a href=\"plugin-install.php?tab=plugin-information&amp;plugin=google-analytics-dashboard-for-wp&amp;_wpnonce=75a92ffb2a&amp;TB_iframe=true&amp;width=600&amp;height=800\" class=\"thickbox open-plugin-details-modal\" aria-label=\"Install Google Analytics Dashboard for WP\">(Install)</a></li></ul></div>", "no");
