INSERT INTO `wp_postmeta` VALUES("721", "151", "credits_1", "<small>Art-Jan maakt wat hij maakt omdat hij mensen wil raken. Of hij daar nu beeldende poezie, fysiek toneel, muziek, moderne schrijvers of Shakespeare voor in moet zetten zal hem Syberisch wezen. “Zolang het maar met mensen en voor mensen is en ik beestachtig gepassioneerd tekeer mag gaan in het theater”, aldus Art-Jan.</small>\r\n\r\n<small><a href=\"http://artjandevries.nl\" target=\"_blank\">Website Art Jan</a></small>");
