INSERT INTO `wp_postmeta` VALUES("2574", "229", "credits_1", "<small><strong>Concept en regie:</strong> Jakop Ahlbom. <strong>Dramaturgie:</strong> Judith Wendel <strong>Spel:</strong> Luc van Esch, Yannick Greweldinger, Judith Hazeleger, Silke Hundertmark, Sofieke de Kater, Gwen Langenberg, Thomas van Ouwerkerk, Reinier Schimmel. <strong>Vormgeving: </strong>Douwe Hibma, Jakop Ahlbom, Remco Gianotten (assistent). <strong>Muziekontwerp:</strong> Wim Conradi, Bauke Moerman. <strong>Kostuumontwerp:</strong> Esmee Thomassen, Kyra Wessel (assistent). <strong>Speciale props en make-up:</strong> Rob Hillenbrink. <strong>Make-up &amp; haarwerk:</strong> Nienke Algra. <strong>Lichtontwerp: </strong>Yuri Schreuders. <strong>Techniek 2014/15:</strong> Niek Snel, Yuri Schreuders, Allard Vonk, Michel van der Weijden, Remco Gianotten, Prem Scholte Albers. <strong>Techniek 2015/16:</strong> Prem Scholte Albers, Remco Gianotten, Alfred van der Meulen, Yuri Schreuders, Tom Vollebregt, Allard Vonk, Michel van der Weijden. <strong>Zakelijke leiding &amp; verkoop:</strong> Wilma Kuite. <strong>Productie:</strong> productiecollectief/ Janneke den Engelsen. <strong>Tourmanager 2014/2015:</strong> Emmalot Morel. <strong>Publiciteit 2014/2015:</strong> Alles voor de Kunsten/ Christel Gouweleeuw, Nina Angkotta. <strong>Publiciteit 2015/16:</strong> Alles voor de Kunsten. <strong>Educatie:</strong> Laura Liefting. <strong>Trailer:</strong> Patrick Minks, Jaap Veldhoen. <strong>Scenefotografie:</strong> Sanne Peper. <strong>Posterbeeld:</strong> Paulina Matusiak &amp; Eddy Wentink. <strong>Grafische vormgeving:</strong> Paulina Matusiak.<strong>Met dank aan</strong>: Emmalot Morel, Christel Gouweleeuw, Hans Brouwer, Monique van Schendelen, Jan Doense, Woedy Woet, Caroline Bleeker, Durk Hibma, Vito van Lent, Michiel Slee, David Tim, Tessa Verbei, Roelof Vossebeld, Freerk Wieringa, Toneelschuur, Theater aan het Spui, Schouwburg Amstelveen, Schouwburg De Lawei, Theater De Stoep, Theaters Tilbur