INSERT INTO `wp_postmeta` VALUES("4464", "342", "credits_1", "<small>Concept, tekst &amp; spel: <strong>Wart Kamps</strong> |Regie: <strong>Bram Jansen</strong> |Vormgeving: <strong>Robin Vogel</strong>  Dramaturgie: <strong>Pol Eggermont</strong> |Script-advies: <strong>Lykele Muus</strong> |Kostuums: <strong>Esmee Thomassen</strong> |Poppenontwerp: <strong>Tamar Stalenhoef</strong> |Choreografie: <strong>Ryan Djojokarso</strong> |Geluidsontwerp: <strong>Jorg Schellekens</strong> |Techniek/ lichtontwerp: <strong>Julius Geleijnse</strong> |Foto: <strong>Joost de Haas</strong>  |Grafisch ontwerp: <strong>OVERHAUS</strong> |Producent: <strong>Alles voor de Kunsten / Wilma Kuite</strong>\r\n\r\nMet dank aan: <strong>Don Duyns</strong>, <strong>René M Broeders, Eveline Karelse, Yfke Koolhaas (Coppers en Co), Joost Lubach, Tim Kamps, Rob Klinkenberg</strong> en <strong>Dr. Bram van Genderen</strong>.</small>\r\n\r\n<small> Première: 20 oktober 2016 Toneelschuur Haarlem. Tournee t/m 22 januari 2017. </small>");
