INSERT INTO `wp_postmeta` VALUES("4472", "342", "alinea_4", "<em>De Goede Richard III </em>is de eerste solovoorstelling van Wart Kamps waarin hij op geheel eigen wijze Shakespeares’ universum binnentreedt, soms lichtvoetig, soms emotioneel, soms humoristisch, en dan weer serieus.");
