INSERT INTO `wp_postmeta` VALUES("5756", "386", "credits_1", "<small><strong>Initiatief</strong> Boost producties. <strong>Regie</strong> Jakop Ahlbom. <strong>Circusregie</strong> Lennie Visser. <strong>Artiesten</strong> Galatee Auzanneau, Marieke Thijssen, Aurel Renault, Marius Pohlmann en David Mupanda. <strong>Toneelbeeld</strong> Jelle Engel. <strong>Props </strong>Mariëlle Heessels. <strong>Geluidsontwerp</strong> Wim Conradi. <strong>Lichtontwerp</strong> Maarten Verheggen. <strong>Techniek</strong> Ramses Nieuwenhuizen. </small>");
